import confLogo from './images/coffee.jpg';
class Badge extends React.Component {
	render(){
		return (
		<div>
			<div>
				<img src= "confLogo" alt="Logo"/>
			</div>


			<div>
				<img src= "" alt="Avatar"/>
				<h1>Daniel <br/> Bermudez </h1>
			</div>


			<div>
				<p> Frontend Engineer </p>
				<p>@DanielBermudez </p>
			</div>

			<div>
				#platziconf
			</div>

		</div>);
	}
}

export default Badge;
